// Get input JSON
const data = $json;

// If languages array exists, dedupe it
if (data.languages) {
  data.languages = [...new Set(data.languages.map(l => JSON.stringify(l)))]
    .map(str => JSON.parse(str));
}

// Keep the same single item, just with deduped languages
return [{ json: data }];

