// Input: an item with a property "subtitle" that contains the .srt text
// Output: cleaned text without timestamps or sequence numbers

const input = items[0].json.subtitle;

// Remove subtitle numbers (lines with only digits)
let cleaned = input.replace(/^\d+\s*$/gm, '');

// Remove timestamp lines (00:00:00,000 --> 00:00:00,000)
cleaned = cleaned.replace(/^\d{2}:\d{2}:\d{2},\d{3} --> \d{2}:\d{2}:\d{2},\d{3}\s*$/gm, '');

// Remove extra newlines
cleaned = cleaned.replace(/\n{2,}/g, '\n').trim();

return [{ json: { text: cleaned } }];

